import * as akamai from "@pulumi/akamai";

const provider_demo = new akamai.EdgeHostName("provider-demo", {
    contractId: "ctr_1-AB123",
    edgeHostname: "www.example.org.edgesuite.net",
    groupId: "grp_123",
    productId: "prd_Object_Delivery",
});
```
